mapper.select:
select 

,

from exam_record
where exam_id = #{examId,jdbcType=INTEGER}
  and stu_id = #{stuId,jdbcType=INTEGER}
  and question_id = #{questionId,jdbcType=INTEGER}

mapper.delete:
delete from exam_record
where exam_id = #{examId,jdbcType=INTEGER}
  and stu_id = #{stuId,jdbcType=INTEGER}
  and question_id = #{questionId,jdbcType=INTEGER}

mapper.update:
update exam_record


    final_score = #{finalScore,jdbcType=DECIMAL},


    answer = #{answer,jdbcType=LONGVARCHAR},


where exam_id = #{examId,jdbcType=INTEGER}
  and stu_id = #{stuId,jdbcType=INTEGER}
  and question_id = #{questionId,jdbcType=INTEGER}

update exam_record
set final_score = #{finalScore,jdbcType=DECIMAL},
  answer = #{answer,jdbcType=LONGVARCHAR}
where exam_id = #{examId,jdbcType=INTEGER}
  and stu_id = #{stuId,jdbcType=INTEGER}
  and question_id = #{questionId,jdbcType=INTEGER}

update exam_record
set final_score = #{finalScore,jdbcType=DECIMAL}
where exam_id = #{examId,jdbcType=INTEGER}
  and stu_id = #{stuId,jdbcType=INTEGER}
  and question_id = #{questionId,jdbcType=INTEGER}
